    private void SignUpBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SignUpBtnActionPerformed
        // Get values from form fields
        String fullName = FullNameTxt.getText().trim();
        String email = EmailTxt.getText().trim();
        String password = new String(PasswordTxt.getPassword());
        boolean isAdmin = UserCheckBox.isSelected();
        
        // Validate input fields
        if (fullName.isEmpty() || email.isEmpty() || password.isEmpty()) {
            JOptionPane.showMessageDialog(this, 
                "Please fill in all fields", 
                "Validation Error", 
                JOptionPane.ERROR_MESSAGE);
            return;
        }
        
        // Validate email format
        if (!emailMatchesRole(email, isAdmin)) {
            JOptionPane.showMessageDialog(this, 
                "Invalid email format for " + (isAdmin ? "admin" : "user") + ".\n" +
                "Email must end with: " + (isAdmin ? "@admin.com" : "@user.com"), 
                "Validation Error", 
                JOptionPane.ERROR_MESSAGE);
            return;
        }
        
        // Check if email already exists
        UserDao userDao = new UserDao();
        if (userDao.emailExists(email)) {
            JOptionPane.showMessageDialog(this, 
                "This email address is already registered.\n" +
                "Please use a different email or login instead.", 
                "Email Already Exists", 
                JOptionPane.WARNING_MESSAGE);
            return;
        }
        
        // Create and save user
        User user = new User(fullName, email, password, isAdmin);
        boolean success = userDao.saveUser(user);
        
        if (success) {
            JOptionPane.showMessageDialog(this, 
                "Registration successful! You can now login with your credentials.", 
                "Success", 
                JOptionPane.INFORMATION_MESSAGE);
                
            // Clear form fields
            clearFields();
                
            // Redirect to login form
            Login loginForm = new Login();
            loginForm.setVisible(true);
            this.dispose();
        } else {
            JOptionPane.showMessageDialog(this, 
                "Failed to create account. Please try again later.", 
                "Registration Failed", 
                JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_SignUpBtnActionPerformed

