    private void UserCheckBoxActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_UserCheckBoxActionPerformed
        // Update email domain when admin checkbox state changes
        String email = EmailTxt.getText().trim();
        if (!email.isEmpty()) {
            if (email.contains("@")) {
                String[] parts = email.split("@");
                if (parts.length > 0) {
                    String username = parts[0];
                    String newEmail = username;
                    
                    if (UserCheckBox.isSelected()) {
                        newEmail += "@admin.com";
                    } else {
                        newEmail += "@user.com";
                    }
                    
                    EmailTxt.setText(newEmail);
                }
            } else {
                // No @ symbol in email, just add the domain
                if (UserCheckBox.isSelected()) {
                    email = email + "@admin.com";
                } else {
                    email = email + "@user.com";
                }
                EmailTxt.setText(email);
            }
        }
    }//GEN-LAST:event_UserCheckBoxActionPerformed

    /**
     * Validates that email format matches the selected role
     * @param email Email to validate
     * @param isAdmin Whether admin role is selected
     * @return true if email matches required format, false otherwise
     */
    private boolean emailMatchesRole(String email, boolean isAdmin) {
        if (email.isEmpty()) {
            return false;
        }
        String suffix = isAdmin ? "@admin.com" : "@user.com";
        return email.endsWith(suffix) && email.indexOf('@') == email.lastIndexOf('@');
    }
    
    /**
     * Clear all form fields
     */
    private void clearFields() {
        FullNameTxt.setText("");
        EmailTxt.setText("");
        PasswordTxt.setText("");
        UserCheckBox.setSelected(false);
    }

